    private void mnuRegistrarClienteActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_mnuRegistrarClienteActionPerformed
        Frm_RegistrarCliente objFrmRC = new Frm_RegistrarCliente();
        boolean mostrar = true;
        for (int i = 0; i < contenedor.getComponentCount(); i++) {
            if (objFrmRC.getClass().isInstance(contenedor.getComponent(i))) {
                mostrar = false;
            }
        }
        if (mostrar == true) {
            contenedor.add(objFrmRC);
            objFrmRC.show();
        } else {
            JOptionPane.showMessageDialog(null, "La ventana ya se encuentra abierta", "Mensaje", JOptionPane.OK_OPTION);
        }
    }//GEN-LAST:event_mnuRegistrarClienteActionPerformed

