    private void btnCalcularActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnCalcularActionPerformed
        try {
            int año = Integer.parseInt(txtAño.getText());
            int contador[] = Cliente.consultarNumClientesLSAño(año);
            txtEnero.setText(String.valueOf(contador[0]));
            txtFebrero.setText(String.valueOf(contador[1]));
            txtMarzo.setText(String.valueOf(contador[2]));
            txtAbril.setText(String.valueOf(contador[3]));
            txtMayo.setText(String.valueOf(contador[4]));
            txtJunio.setText(String.valueOf(contador[5]));
            txtJulio.setText(String.valueOf(contador[6]));
            txtAgosto.setText(String.valueOf(contador[7]));
            txtSeptiembre.setText(String.valueOf(contador[8]));
            txtOctubre.setText(String.valueOf(contador[9]));
            txtNoviembre.setText(String.valueOf(contador[10]));
            txtDiciembre.setText(String.valueOf(contador[11]));
        } catch (Exception e) {
            JOptionPane.showMessageDialog(this, "Ha ocurrido un error inesperado: " + e.getLocalizedMessage(), "ERROR DEL SISTEMA", JOptionPane.ERROR_MESSAGE);
        }
    }//GEN-LAST:event_btnCalcularActionPerformed

