    private void btnListarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnListarActionPerformed
        try {
        DefaultTableModel dtmListado = new DefaultTableModel();
        dtmListado.addColumn("Día");
        dtmListado.addColumn("Número de clientes");

        int mes = JMes.getMonth() + 1;
        int año = JAño.getYear();
        

        Calendar fecha = Calendar.getInstance();
        fecha.set(año, mes, 0);
        int numDias = fecha.getActualMaximum(Calendar.DAY_OF_MONTH);//Valor maximo de ese campo
        int numClientes[] = Cliente.consultarNumClientesDiarios(año, mes, numDias);

        for (int i = 0; i < numDias; i++) {
            dtmListado.addRow(new Object[]{(i + 1), numClientes[i]});
        }

        tblListado.setModel(dtmListado);
        } catch (Exception e) {
            JOptionPane.showMessageDialog(this, "Ha ocurrido un error inesperado: " + e.getLocalizedMessage(), "ERROR DEL SISTEMA", JOptionPane.ERROR_MESSAGE);
        }
        
    }//GEN-LAST:event_btnListarActionPerformed

