    private void btnRegistrarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnRegistrarActionPerformed
        try {
            if (cboSexo.getSelectedIndex() == 0 || cboZona.getSelectedIndex() == 0 || cboTipo.getSelectedIndex() == 0 || cboLinea.getSelectedIndex() == 0) {
                JOptionPane.showMessageDialog(this, "Por favor, seleccionar todos los campos", "ERROR", JOptionPane.ERROR_MESSAGE);
            } else {
                String linea = cboLinea.getSelectedItem().toString();
                String dni = txtDNI.getText();
                TCola cola = null;
                if (linea.equalsIgnoreCase("Salud-bienestar")) {
                    cola = LineaSalud_Normal.consultar();
                } else {
                    cola = LineaBelleza_Normal.consultar();
                }
                if (Cliente.validarDNI(cola, dni) == -1) {
                    Cliente objCli = new Cliente();
                    String nombre = txtNombre.getText();
                    int edad = Integer.parseInt(txtEdad.getText());
                    String sexo = cboSexo.getSelectedItem().toString();
                    String zona = cboZona.getSelectedItem().toString();
                    String tipo = cboTipo.getSelectedItem().toString();
                    objCli.setDni(dni);
                    objCli.setNombre(nombre);
                    objCli.setEdad(edad);
                    objCli.setSexo(sexo);
                    objCli.setZona(zona);
                    objCli.setTipo(tipo);
                    objCli.setLinea(linea);
                    if (linea.equalsIgnoreCase("Salud-bienestar")) {
                        LineaSalud_Normal.agregar(objCli);
                    } else {
                        LineaBelleza_Normal.agregar(objCli);
                    }
                    JOptionPane.showMessageDialog(this, "Cliente registrado exitosamente", "INFORME", JOptionPane.INFORMATION_MESSAGE);
                    txtDNI.setText("");
                    txtNombre.setText("");
                    txtEdad.setText("");
                    cboSexo.setSelectedIndex(0);
                    cboZona.setSelectedIndex(0);
                    cboTipo.setSelectedIndex(0);
                    cboLinea.setSelectedIndex(0);
                } else {
                    JOptionPane.showMessageDialog(this, "Cliente ya existe en cola", "ERROR", JOptionPane.ERROR_MESSAGE);
                }
            }
        } catch (Exception e) {
            JOptionPane.showMessageDialog(this, "Ha ocurrido un error inesperado: " + e.getLocalizedMessage(), "ERROR DEL SISTEMA", JOptionPane.ERROR_MESSAGE);
        }
    }//GEN-LAST:event_btnRegistrarActionPerformed

