    private void btnDeterminarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnDeterminarActionPerformed
        EcuacionCuadratica objEcuacion = new EcuacionCuadratica();
        double valorIngresado1=Double.parseDouble(txtA.getText());
        double valorIngresado2=Double.parseDouble(txtB.getText());
        double valorIngresado3=Double.parseDouble(txtC.getText());
        valorIngresado1=objEcuacion.getA();
        valorIngresado2=objEcuacion.getB();
        valorIngresado3=objEcuacion.getC();
        String raiz1=String.valueOf(objEcuacion.resultado1());
        lblResultado1.setText(raiz1);
        String raiz2=String.valueOf(objEcuacion.resultado2());
        lblResultado2.setText(raiz2);
    }//GEN-LAST:event_btnDeterminarActionPerformed

