    private void btnCalcularAreaPerimetroActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnCalcularAreaPerimetroActionPerformed
        float b,h,a,p;
        b=Float.parseFloat(txtBase.getText());
        h=Float.parseFloat(txtAltura.getText());
        
        Rectangulo obj=new Rectangulo(b,h);
        
        a=obj.calcularArea();
        p=obj.calcularPerimetro();
        
        lblArea.setText(String.valueOf(a)+" unidades cuadradas");
        lblPerimetro.setText(String.valueOf(p)+ " unidades");
    }//GEN-LAST:event_btnCalcularAreaPerimetroActionPerformed

