    private void cboTipoActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_cboTipoActionPerformed
        String tipo;
        tipo=cboTipo.getSelectedItem().toString();
        
        if (tipo.equalsIgnoreCase("B")) {
            lblLetra.setText("B:");
            lblLetraRpta.setText("P:");
            btnHallar.setText("Hallar P");
        } else {
            lblLetra.setText("C:");
            lblLetraRpta.setText("Q:");
            btnHallar.setText("Hallar Q");
        }
        txtA.setText("");
        txtValor.setText("");
        lblRpta.setText("???");
    }//GEN-LAST:event_cboTipoActionPerformed

