    private void btnHallarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnHallarActionPerformed
        
        String tipo;
        int a;
        
        tipo=cboTipo.getSelectedItem().toString();
        a=Integer.parseInt(txtA.getText());
        
        if(tipo.equalsIgnoreCase("B")){
            int b;
            float p;
            b=Integer.parseInt(txtValor.getText());
            B obj = new B();
            obj.setA(a);
            obj.setB(b);
            p=obj.p();
            lblRpta.setText(String.valueOf(p));
        }
        else {
            float c,q;
            c=Float.parseFloat(txtValor.getText());
            C obj = new C();
            obj.setA(a);
            obj.setC(c);
            q=obj.q();
            lblRpta.setText(String.valueOf(q));
        }
    }//GEN-LAST:event_btnHallarActionPerformed

